
public class WorkDay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int day=1;
		boolean  workDay=true;

	
		while(workDay) {
			if(day==6) {
			
			System.out.println("I do not need a day off any more");
			workDay=false;
			}else {
				System.out.println("I need a day off");
			}
			
			day++;
			
	
		}
		
	
		}
	}
		

	

