package com.class05;

public class LogicalNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		boolean b1=!true;
		boolean b2=!false;
		System.out.println(b1);
		System.out.println(b2);
		
		boolean traffic=true;
		if(!traffic) {
			System.out.println("Hello");
		}else {
			System.out.println("Bye");
			
			
		}
		
	}

}
