package com.class07;

public class Forloop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=1; i<=100; i++)  {
			System.out.print(i+" ");
			
			
		}
			//************************************************************
			
			
			for(int a=100; a>=1;  a--) {
			System.out.println(a);
			}
			
		
		
	}
}
		
		
		
		
	


