package com.class07;

public class LoopAnd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int z=1;
		while(z<=20) {
	
		System.out.println(z);
		z+=2;
	}
//*************************************
	int q=2;
	while(q<=20) {
		if(q%2==0) {
			System.out.print(q);
			 
		}
		q++;  
		//(++ always comes after if line.)
	}
		
		
		
}
}