package com.class07;

public class WhileLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i=0;
		while(i<=20) {
			System.out.println(i);
			i++;
			
		}
		
		int y=10;
		while(y<=30 ) {
			System.out.print(y+" ");
			y++;
		}
//****************************************************************		
		int t=50;
		while(t>=20) {
			System.out.print(t+" ");
			t++;
			
		}
		
		
		
	}
	
	
	

}
