package com.classManupulation;

public class Replace {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String str12="Hello Dan,how are you Dan,How you been?";
		System.out.println(str12.replace("how", "HOW"));
		System.out.println(str12.replaceFirst("Dan", "ABDIL"));
		System.out.println(str12.replace("o", "O"));
		
	}

}
