package com.review4;

public class NestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int floor=1; floor<=5; floor++) {
			for(int room=1; room<=6; room++) {
			System.out.print(floor +"." +room+" ");
			System.out.print(floor+" ");
			}
			System.out.println();
			
		
	}

}

}
